INSERT INTO `tp_cases` (`id`,`name`,`img`,`editorValue`,`num`,`home`,`sort`,`title`,`description`,`keywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('2','96V 250ah lithium iron phosphate energy storage sy','/tmp/uploads/20191022/7f5dc6df6149514d9276b81fc4a967e5.jpg','&lt;p&gt;The 96V 250ah lithium iron phosphate energy storage system can be applied to the battery power supply system of equipment room. The system voltage is 96V, the capacity is 24000wh, and the service life is 5 years. The BMS adopts the master-slave structure design, which is more intelligent, safer, and can be customized as required.&lt;/p&gt;','80','1','0','96V 250ah lithium iron phosphate energy storage system-Solution-Hebei Xin Dong Li New Energy Science And Technology Co.Ltd','The 96V 250ah lithium iron phosphate energy storage system can be applied to the battery power supply system of equipment room. The system voltage is 96V, the capacity is 24000wh, and the service life is 5 years. ','','1569309031','1574229796','0','0','1');
