INSERT INTO `tp_cases` (`id`,`name`,`img`,`editorValue`,`num`,`home`,`sort`,`title`,`description`,`keywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('12','Solar Projects','/tmp/uploads/20191225/d4198c122c7ca73ecc2a42009cffb926.png','&lt;p class=&quot;first&quot;&gt;With over 20GW solar panels installed in more than 100 countries around the world, our Solar panels have a leading track-record of reliability and performance. Our solar panels have achieved high-performance in nearly every climate and environmental condition imaginable&lt;img src=&quot;http://7j1y9l.com1.z0.glb.clouddn.com/image/k4koomwo_11wnh1d00dxw5e02ca6b21d62.png&quot; title=&quot;9c4b87b7029c793949474eac99d1324fea316f36.png&quot; alt=&quot;9c4b87b7029c793949474eac99d1324fea316f36.png&quot; width=&quot;431&quot; height=&quot;259&quot;/&gt;&lt;/p&gt;&lt;p class=&quot;last&quot;&gt;Our customers and partners span the globe and include distributors, system integrators, construction companies, developers and utilities that deliver clean solar energy to local businesses and communities. Read more about some of the many global projects, and don&amp;#39;t hesitate to contact us with questions about a potential solar project in your part of the world!&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;	&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','21','1','0','Industrial lithium battery-Customized lithium battery-Hebei Xin Dong Li New Energy Science And Technology Co.Ltd','','Industrial lithium battery,Customized lithium battery','1577241208','1577244881','0','0','1');
INSERT INTO `tp_cases` (`id`,`name`,`img`,`editorValue`,`num`,`home`,`sort`,`title`,`description`,`keywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('13','Power Plants','/tmp/uploads/20191225/56f124d3379a525cbbff10684a002d65.jpg','&lt;p class=&quot;first last&quot;&gt;A utility-scale solar power plant is not just a long-term investment; it&amp;#39;s a two-decade partnership. With over 20GW of solar panels delivered worldwide, we have worked with hundreds of developers, EPCs, and utility companies in dozens of countries to bring large-scale solar projects to life.&lt;/p&gt;&lt;p class=&quot;first last&quot; style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;http://7j1y9l.com1.z0.glb.clouddn.com/image/k4korc4o_11wnh1d00e005e02cae921d62.jpg&quot; title=&quot;b4ff595c9e731b08e9c677869c61f4952c8b9c5b.jpg&quot; alt=&quot;b4ff595c9e731b08e9c677869c61f4952c8b9c5b.jpg&quot; width=&quot;393&quot; height=&quot;230&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','19','1','0','Industrial lithium battery-Customized lithium battery-Hebei Xin Dong Li New Energy Science And Technology Co.Ltd','','Industrial lithium battery,Customized lithium battery','1577241331','1577241331','0','0','1');
INSERT INTO `tp_cases` (`id`,`name`,`img`,`editorValue`,`num`,`home`,`sort`,`title`,`description`,`keywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('14','Solar for Homes','/tmp/uploads/20191225/df000f018b0ebf7c8152cac925d1c975.jpg','&lt;p class=&quot;first last&quot;&gt;As a homeowner, solar panels could provide a secure, long-term investment that reduces both your carbon footprint and your electric bills. As the world&amp;#39;s largest solar panel manufacturer, our Solar is trusted by thousands of homeowners around the world.&lt;/p&gt;&lt;p class=&quot;first last&quot; style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;http://7j1y9l.com1.z0.glb.clouddn.com/image/k4kot3eg_32k3odrlqx6o5e02cb3b62afb.jpg&quot; title=&quot;3b4737e1f6fb1701a3ad2e4876b540160a8d7cfd.jpg&quot; alt=&quot;3b4737e1f6fb1701a3ad2e4876b540160a8d7cfd.jpg&quot; width=&quot;375&quot; height=&quot;223&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','17','1','0','Industrial lithium battery-Customized lithium battery-Hebei Xin Dong Li New Energy Science And Technology Co.Ltd','','Industrial lithium battery,Customized lithium battery','1577241416','1577244786','0','0','1');

